const DATE_TO_COUNTDOWN_TO="June 11, 2023 11:49 am",countDownDate=new Date(DATE_TO_COUNTDOWN_TO).getTime();document.getElementById("countdown-date").innerHTML=DATE_TO_COUNTDOWN_TO;const plural=(e,n)=>1===n?e:`${e}s`;let countdownInterval;const updateCounter=()=>{const e=(new Date).getTime(),n=countDownDate-e,t=Math.floor(n/864e5),o=Math.floor(n%864e5/36e5),a=Math.floor(n%36e5/6e4),r=document.getElementById("days");r.innerHTML="";const u=document.createElement("div");u.innerHTML=`${t} ${plural("day",t)}`,r.append(u);const d=document.createElement("div");d.innerHTML=`${o} ${plural("hour",o)}`,r.append(d);const l=document.createElement("div");l.innerHTML=`${a} ${plural("minute",a)}`,r.append(l),n<0&&(clearInterval(countdownInterval),r.innerHTML="It's here!")};countdownInterval=setInterval(updateCounter,1e3),updateCounter();